package com.landleaf.mail.domain.param;

import lombok.Data;
import org.springframework.core.io.InputStreamSource;

/**
 * 邮件基本
 *
 * @author yue lin
 * @since 2023/7/26 15:33
 */
@Data
public abstract class AbstractMail {


    static final String IMAGE_BASE64 = "data:image/png;base64,";

    /**
     * 目标邮箱
     */
    private String to;

    /**
     * 邮件主题
     */
    private String subject;

    /**
     * 邮件模板
     */
    private String mailFormat;

    /**
     * 邮件模板-类型，用于查询邮件模板
     * 1账号创建2密码重置3租户创建
     */
    private Long mailFormatType;

    /**
     * 邮件模板参数-图片Base64
     */
    private String image;

    /**
     * 附件名
     */
    private String attachmentName;

    /**
     * 附件
     */
    private InputStreamSource attachmentResource;

    /**
     * 将实体转为邮件文本
     *
     * @return 邮件文本
     */
    public abstract String format();

}
